  selector: 'app-titulo',
  templateUrl: './titulo.component.html',
  styleUrls: ['./titulo.component.css']
})
export class TituloComponent implements OnInit {
  @Input() titulo: string = '';

  constructor() { }

  ngOnInit() {
  }

}

 <h2>
  {{ titulo }}
 </h2>
 <hr>


 <div class="d-flex  p-3 my-3 text-white bg-secondary rounded box-shadow">
